el\n      - chiroC6,nolabel\n      - chiroC7,nolabel\n      - chiroT1,nolabel\n      - chiroT2,nolabel\n      - chiroT3,nolabel\n      - chiroT4,nolabel\n      - chiroT5,nolabel\n      - chiroT6,nolabel\n      - chiroT7,nolabel\n      - chiroT8,nolabel\n      - chiroT9,nolabel\n      - chiroT10,nolabel\n      - chiroT11,nolabel\n      - chiroT12,nolabel\n      - chiroK1_Lt,nolabel\n      - chiroK1_Rt,nolabel\n      - chiroK2_Lt,nolabel\n      - chiroK2_Rt,nolabel\n      - chiroK3_Lt,nolabel\n      - chiroK3_Rt,nolabel\n      - chiroK4_Lt,nolabel\n      - chiroK4_Rt,nolabel\n      - chiroK5_Lt,nolabel\n      - chiroK5_Rt,nolabel\n      - chiroK6_Lt,nolabel\n      - chiroK6_Rt,nolabel\n      - chiroK7_Lt,nolabel\n      - chiroK7_Rt,nolabel\n      - chiroK8_Lt,nolabel\n      - chiroK8_Rt,nolabel\n      - chiroK9_Lt,nolabel\n      - chiroK9_Rt,nolabel\n      - chiroK10_Lt,nolabel\n      - chiroK10_Rt,nolabel\n      - chiroK11_Lt,nolabel\n      - chiroK11_Rt,nolabel\n      - chiroK12_Lt,nolabel\n      - chiroK12_Rt,nolabel\n      - chiroL1,nolabel\n      - chiroL2,nolabel\n      - chiroL3,nolabel\n      - chiroL4,nolabel\n      - chiroL5,nolabel\n      - chiroCoccyx,nolabel\n...\n', '', 'csChiro', '', '$(document).ready(function() {\r\n\r\n  if (!$(\".mousehelper\").length) \r\n    $(\"body\").append(\'<div class=\"helper hide mousehelper\"></div>\');\r\n\r\n  if (!$(\".svgMenuSingle\").length)\r\n    $(\"body\").append(\'\\\r\n      <ul class=\"dropdown-menu svgMenuSingle\" role=\"menu\" style=\"display:none;position:absolute;\" >\\\r\n          <li id=\"title><span tabindex=\"-1\" style=\"margin-left:20px;margin-right:10px;font-weight:700\">Menu</span></li>\\\r\n          <li class=\"dropdown-divider\"></li>\\\r\n      </ul>\');\r\n  if (!$(\".svgMenuMulti\").length)\r\n    $(\"body\").append(\'\\\r\n      <ul class=\"dropdown-menu svgMenuMulti\" role=\"menu\" style=\"display:none;position:absolute;\" >\\\r\n          <li id=\"title><span tabindex=\"-1\" style=\"margin